INSERT INTO `jos_content` VALUES ('66', 'Why book with Us?', 'why-book-with-us', '', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">First of all We only provide airlines that uses the best and safest Air Crafts in Venezuela, including a new online reservation system that permits real time reservation with up to date prices.</span></p>', '', '1', '3', '0', '44', '2008-10-27 00:00:00', '62', 'Henrik Bratfeldt', '2009-02-10 12:01:05', '62', '0', '0000-00-00 00:00:00', '2008-09-20 04:52:36', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=0\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=en-GB\nkeyref=\nreadmore=', '6', '0', '1', '', '', '0', '711', 'robots=\nauthor=');
