INSERT INTO `jos_content` VALUES ('99', 'At what time does the plane leave?', 'at-what-time-does-the-plane-leave', '', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">\r\n<p style=\"margin: 0in 0in 0pt;\" class=\"MsoNormal\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">The first flights leaves at 8.30 in the morning, and returns everyday at 17.00 hours in the afternoon. If your international flight is arriving late that day you might consider staying in a <a target=\"_blank\" href=\"http://www.caracas.travel\">Hotel close to the airport in Caracas</a>.  </span></p>\r\n</span>', '', '1', '3', '0', '44', '2008-10-27 00:00:00', '62', 'Iveth', '2008-10-28 03:31:49', '62', '0', '0000-00-00 00:00:00', '2008-09-20 20:27:39', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=en-GB\nkeyref=\nreadmore=', '4', '0', '8', '', '', '0', '656', 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES ('101', 'Is Venezuela a place for children?', 'is-venezuela-a-place-for-children', '', '<p><span style=\"font-size: 10pt; font-family: Arial;\"><span style=\"color: #888888;\"><span style=\"font-size: 10pt; font-family: Arial;\"><span style=\"color: #888888;\"><strong></strong></span></span></span><strong></strong></span><strong></strong></p>\r\n<p>Y<span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">es, there are often children here and they seem to enjoy there vacation more than there parents. Your children can safely play here in a very safe environment including discovering many of the great destinations that Venezuela offers.</span></p>', '', '1', '3', '0', '44', '2008-10-21 00:00:00', '62', 'Oliver', '2009-02-10 10:56:04', '62', '0', '0000-00-00 00:00:00', '2008-09-20 20:29:07', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=en-GB\nkeyref=\nreadmore=', '5', '0', '7', '', '', '0', '592', 'robots=\nauthor=');
