INSERT INTO `jos_content` VALUES ('105', 'Will my mobile phone work, can I access the internet for mails?', 'will-my-mobile-phone-work-can-i-access-the-internet-for-mails', '', '<span style=\"font-size: 10pt; font-family: Arial;\"><span><span style=\"color: #000000;\"><o:p>\r\n<p>Your mobile phone will likely have signal only some of the time, and in the remote areas there is none. Close to the Airport you will also have WIFI, phones, faxes and email can be used in the local communication center.</p>\r\n</o:p></span></span></span>', '', '1', '3', '0', '44', '2008-10-27 00:00:00', '62', 'Henrik', '2009-02-20 18:57:56', '62', '0', '0000-00-00 00:00:00', '2008-09-20 20:33:36', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=en-GB\nkeyref=\nreadmore=', '6', '0', '5', '', '', '0', '630', 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES ('106', 'What kind of activities is there to do in Caracas?', 'what-kind-of-activities-is-there-to-do-in-caracas', '', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Activities in and around Caracas and generally in Venezuela are many and varied. For example great water sports are possible: Kitesurfing, Snorkeling, Scuba Diving, Bone fishing, and Windsurfing. We will also try to accommodate any other activities you may request at time of booking.</span></p>', '', '1', '3', '0', '44', '2008-10-27 00:00:00', '62', 'Henrik', '2009-02-10 10:55:08', '62', '0', '0000-00-00 00:00:00', '2008-09-20 20:34:37', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=en-GB\nkeyref=\nreadmore=', '8', '0', '4', '', '', '0', '696', 'robots=\nauthor=');
INSERT INTO `jos_content` VALUES ('110', 'Venezuela Flights and Airline companies 2010', 'venezuela-flights-and-airlines-2009', '', '<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: 10pt;\">\r\n<p><strong><img height=\"196\" width=\"300\" src=\"images/stories/maiquetia_airport_venezuela.jpg\" alt=\"maiquetia_airport_venezuela\" style=\"float: right; margin: 3px; border: #bbbbbb 2px solid;\" /></strong></p>\r\n<p><span><span style=\"font-family: arial,helvetica,sans-serif; font-size: 10pt;\">The best offers from Venezuela Flights, now you can finally make and online reservation to almost any destination in Venezuela....<br />Flights in Venezuela. Find and compare the offers for cheap flights of low cost and national airlines. Select the flying out and arriving airport and in only a few steps you get a large selection of the best flight fares applied by low cost and national airlines. You can book your flight online in full security and purchase the flight tickets in only a few minutes.</span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"><a target=\"_blank\" href=\"en/book-your-flight-now\" title=\"Book now\">Make a Reservation!</a>  <a href=\"en/how-to-get-to-los-roques/sitemap\">Sitemap</a></span></span></span></span></span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></p>\r\n</span></span>', '', '1', '4', '0', '25', '2009-02-09 00:00:00', '62', 'Venezuela National Flights', '2010-03-01 17:11:47', '62', '0', '0000-00-00 00:00:00', '2008-09-22 23:50:48', '0000-00-00 00:00:00', '', '', 'show_title=1\nlink_titles=1\nshow_intro=0\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=0\nshow_create_date=0\nshow_modify_date=1\nshow_pdf_icon=0\nshow_print_icon=0\nshow_email_icon=0\nlanguage=en-GB\nkeyref=\nreadmore=', '67', '0', '4', 'Aerotuy, lta, Chapi, air, Makoro, tours', 'Aerotuy - Chapi air - Makoro your best choice when flying to Los Roques.', '0', '21501', 'robots=\nauthor=Henrik Bratfeldt');
