INSERT INTO `jos_content` VALUES ('155', 'Full Day VIP en Los Roques', 'full-day-vip-en-los-roques', '', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Enjoy of an unforgettable day at the beautiful beaches of Los Roques, prepared yet so that your only preoccupation is to relax and enjoy</span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"> yourself.<img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">We offer attendance from the Airport in Maiquetía, with roundtrip ticket to Los Roques, where you<em><strong> </strong></em>will be received by our Crew. Transfer in boat to the Craskí Key, lunch in the restaurant of<em><strong> </strong></em>Craskí with delicious fresh fish, rice, salad and dessert. Lunch box for the beach equipped with<em><strong> </strong></em>ice, water, soda and 2 cold beers by person, awnings and chairs for beach, one hour of guide snorkeling into the crystalline waters of the key and customized attention of professional bilingual tour guides throughout the trip.</span></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Monday to Thursday Friday, Saturday and Sunday</span></strong></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">From Maiquetia National Airport USD. $280 USD. $295</span></strong></p>', '', '1', '9', '0', '32', '2008-10-27 00:00:00', '62', 'A Day Tour by Raul', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '2008-10-28 03:54:30', '0000-00-00 00:00:00', '', '', 'show_title=\nlink_titles=\nshow_intro=\nshow_section=\nlink_section=\nshow_category=\nlink_category=\nshow_vote=\nshow_author=\nshow_create_date=\nshow_modify_date=\nshow_pdf_icon=\nshow_print_icon=\nshow_email_icon=\nlanguage=\nkeyref=\nreadmore=', '1', '0', '3',