INSERT INTO `jos_jf_content` VALUES ('150', '5', '99', 'content', 'introtext', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">\r\n<div dir=\"ltr\" id=\"result_box\">El primer vuelo sale a las 8,30 de la mañana, y regresa todos los días a las 17.00 horas en la tarde. Si su vuelo internacional llega tarde ese día usted podría considerar la posibilidad de alojarse en un <a target=\"_blank\" href=\"http://www.caracas.travel\">Hotel cerca el aeropuerto en Caracas</a>.  </div>\r\n</span></span>\r\n<div dir=\"ltr\" id=\"result_box\"></div>\r\n</span>', 'b0016489117be41ab4699cdb334c0333', '', '2008-10-29 14:56:54', '62', '1');
