INSERT INTO `jos_jf_content` VALUES ('194', '5', '145', 'content', 'introtext', '<div style=\"text-align: left;\" dir=\"ltr\" id=\"result_box\"><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Tenemos un gran variedad de destinos en Venezuela a disposición de aquellos que buscan experimentar en sus vacaciones de lujo. Nuestras vacaciones están programadas inolvidable y nuestro buen nombre proviene de la increíble servicio prestado a usted por la experiencia de nuestro equipo. Es imposible no disfrutar de esta increíble experiencia de vacaciones rodeado por personas que aman el sol, el mar, la aventura y hacer de sus vacaciones una que usted quiere revivir una y otra vez.</span></div>', '4605bf534f3dc2e6da548854df5e4be8', '', '2008-10-29 13:30:15', '62', '1');
