INSERT INTO `jos_jf_content` VALUES ('408', '3', '70', 'content', 'introtext', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  Während <st1:personname productid=\"a yacht charter\" w:st=\"on\">  ein großes Abenteuer Urlaub </st1:personname>  Erfahrung ist nicht für den täglichen Person, ultimative Luxus kommt mit einem Preis.  Trinkgeld ist seit jeher ein Thema wund, wenn es um die tatsächliche Reise-Industrie und in den meisten VIP-Reise. <o:p></o:p></span></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  Wegen der besonderen <st1:personname productid=\"Charter vacations\" w:st=\"on\">  Ferien </st1:personname>  Aufwendungen werden nur selten all inclusive, so verlassen Sie bitte etwas an den Hausmeister, Koch oder eine helfende Hand, wenn Sie verlassen. </span></span></p>', '38baea938d1777300b362f1990c41139', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">While <st1:personname productid=\"a yacht charter\" w:st=\"on\">a great adventure vacation </st1:personname>experience is not for the everyday person, ultimate luxury does come with a price. Gratuity has always been a sore subject when it comes to the real travel industry and in most VIP travel experiences. <o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">For special <st1:personname productid=\"Charter vacations\" w:st=\"on\">vacations</st1:personname>, expenses are rarely all inclusive, so please leave something to the caretaker, cook or any helping hand when you leave. </span></span></p>', '2009-01-07 21:14:20', '62', '1');
