INSERT INTO `jos_jf_content` VALUES ('438', '3', '83', 'content', 'introtext', '<span style=\"font-family: Arial;\"><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Wo nicht anders vermerkt, Preise pro Person und Flug Hin-und Rückfahrt. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Wenn Sie es wünschen, einen privaten Flugzeug, nehmen Sie bitte Kontakt mit uns auf! </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Die Preise für zusätzliche Aktivitäten wie zB: </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Tauchen und Knochen Angeln, etc. ..  wird zum Zeitpunkt der Buchung.  Details, Preise und Verfügbarkeit werden in gutem Glauben und die, die als zuverlässig gelten, sind aber nicht garantiert werden.  Wir werden die Preise und Verfügbarkeit bei der Anfrage / Buchung. </span></p></span>', 'df0347cca63c8c7a16278010eceb06d2', '<span style=\"font-family: Arial;\">\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Except where indicated, pricing is per person per round trip flight.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">If you wish to rent a private plane, please contact us!</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Pricing for additional activities like:</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Scuba diving and bone fishing, etc... will be calculated at time of booking. Details, rates and availability are shown in good faith and believed to be reliable, but are not guaranteed. We will confirm rates and availability during the inquiry / booking process.</span></p>\r\n</span>', '2009-01-07 21:14:30', '62', '1');
