INSERT INTO `jos_jf_content` VALUES ('458', '3', '99', 'content', 'introtext', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\"><p style=\"margin: 0in 0in 0pt;\" class=\"MsoNormal\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">  Die ersten Flüge verlässt um 8.30 Uhr am Morgen, und liefert täglich um 17.00 Uhr am Nachmittag.  Wenn Ihr Flug ist spät an diesem Tag können Sie prüfen, bei Aufenthalt in einem <a target=\"_blank\" href=\"http://www.caracas.travel\">Hotel in der Nähe zum Flughafen in Caracas.</a> </span></p></span>', 'b0016489117be41ab4699cdb334c0333', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">\r\n<p style=\"margin: 0in 0in 0pt;\" class=\"MsoNormal\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">The first flights leaves at 8.30 in the morning, and returns everyday at 17.00 hours in the afternoon. If your international flight is arriving late that day you might consider staying in a <a target=\"_blank\" href=\"http://www.caracas.travel\">Hotel close to the airport in Caracas</a>.  </span></p>\r\n</span>', '2009-01-07 21:14:34', '62', '1');
