INSERT INTO `jos_jf_content` VALUES ('497', '3', '106', 'content', 'title', 'Welche Aktivitäten gibt es zu tun, in Los Roques?', 'c73dcd31da0b0d890804f6fbba0f4fc6', 'What kind of activities is there to do in Los Roques?', '2009-01-07 21:14:45', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('498', '3', '106', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Aktivitäten in und um Los Roques sind vielfältig.  Zum Beispiel bieten wir Ihnen, die Wassersport: Kitesurfen, Schnorcheln, Tauchen, Angeln Bone und Windsurfen.  Wir werden auch versuchen, alle anderen Aktivitäten können Sie bei der Buchung. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Denken Sie daran, dass Sie die größte National Marine Park in der Karibik, mit hervorragenden unberührten Flora und Fauna über und unter Wasser, so dass dieser Nationalpark sehr interessant im Vergleich zum Rest der Karibik. </span></p>', 'fabc70e9c0f529495beed92b7276039d', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Activities in and around Los Roques are many and varied. For example we offer water sports that include: Kitesurfing, Snorkeling, Scuba Diving, Bone fishing, and Windsurfing. We will also try to accommodate any other activities you may request at time of booking.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Remember that you enter the biggest National Marine Park in the Caribbean, with excellent virgin flora and fauna above and under water, making this national park very interesting compared to the rest of the Caribbean.</span></p>', '2009-01-07 21:14:45', '62', '1');
