INSERT INTO `jos_jf_content` VALUES ('607', '6', '155', 'content', 'title', 'Journée complète en VIP à Los Roques', '7af7ec49b213af4c100bcada2dbd9bfe', 'Full Day VIP en Los Roques', '2009-01-07 21:22:17', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('608', '6', '155', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Profitez d\'une journée inoubliable à des belles plages de Los Roques, encore préparé de sorte que votre seule préoccupation est de se détendre et profiter </span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  vous-même. <img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Nous vous offrons la fréquentation de l\'aéroport de Maiquetía, avec billet aller-retour à Los Roques, si vous <em><strong> </strong></em>  sera reçu par notre équipe.  Transfert en bateau à la clé Craskí, déjeuner dans le restaurant de <em><strong> </strong></em>  Craskí de délicieux poissons frais, de riz, salade et dessert.  Boîte à lunch pour la plage équipée de <em><strong> </strong></em>  glace, eau, sodas et 2 bières par personne froide, les auvents et les chaises de plage, une heure de guide de plongée au tuba dans les eaux cristallines de la clé et l\'attention personnalisée de guides professionnels bilingues dans l\'ensemble du voyage. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>Du lundi au jeudi, vendredi, samedi et dimanche</strong> </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>De l\'aéroport national Maiquetia USD.</strong>  <strong>$ 280 USD.</strong>  <strong>295 $</strong> </span></p>', '0d16661c6770354dd45ce1e186465fbe', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Enjoy of an unforgettable day at the beautiful beaches of Los Roques, prepared yet so that your only preoccupation is to relax and enjoy</span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"> yourself.<img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">We offer attendance from the Airport in Maiquetía, with roundtrip ticket to Los Roques, where you<em><strong> </strong></em>will be received by our Crew. Transfer in boat to the Craskí Key, lunch in the restaurant of<em><strong> </strong></em>Craskí with delicious fresh fish, rice, salad and dessert. Lunch box for the beach equipped with<em><strong> </strong></em>ice, water, soda and 2 cold beers by person, awnings and chairs for beach, one hour of guide snorkeling into the crystalline waters of the key and customized attention of professional bilingual tour guides throughout the trip.</span></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Monday to Thursday Friday, Saturday and Sunday</span></strong></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">From Maiquetia National Airport USD. $280 USD. $295</span></strong></p>', '2009-01-07 21:22:17', '62', '1');
