INSERT INTO `jos_jf_content` VALUES ('678', '6', '154', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">  Aller-retour en vol: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\"> Caracas </st1:place></st1:city>  / Los Roques </st1:personname><span style=\"mso-spacerun: yes\"></span>  255,00 USD $ -- <o:p></o:p></span></span></p><ul><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Prix par personne. </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Les enfants ont une réduction de 30% </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Journée complète 295 $.  Avec tout inclus </span></li><li><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">  Privat air charter, s\'il vous plaît contactez-nous pour une qoute! </span><span style=\"font-size: 10pt;\"></span></span></li></ul><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Parc national de droit d\'entrée n\'est pas inclus, s\'il vous plaît mettre en monnaie locale .... </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  <a href=\"http://www.cacao.travel/en-GB/make-your-reservation-now?task=dobooking&selectedProperty=5\"><strong>Faire une réservation dès maintenant!</strong></a> </span></p>', '75f1ec72d5626eeb475cd5058439a5db', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">Round trip flight: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\">Caracas</st1:place></st1:city> / Los Roques</st1:personname><span style=\"mso-spacerun: yes\">  </span>USD $ 255,00-<o:p></o:p></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size