INSERT INTO `jos_jf_content` VALUES ('687', '6', '83', 'content', 'title', 'Quelles sont vos conditions?', 'ecee116f752a9b535a452b1981bc6cd8', 'What are your terms?', '2009-01-07 21:22:41', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('688', '6', '83', 'content', 'introtext', '<span style=\"font-family: Arial;\"><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Sauf indication contraire, les prix sont par personne et par aller-retour. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Si vous désirez louer un avion privé, s\'il vous plaît contactez-nous! </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Prix pour des activités comme: </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Plongée sous-marine et d\'os, de pêche, etc ..  seront calculés au moment de la réservation.  Détails, tarifs et disponibilité sont présentés de bonne foi et considérées comme fiables, mais ne sont pas garantis.  Nous vous confirmerons les prix et la disponibilité au cours de l\'enquête / procédure de réservation. </span></p></span>', 'df0347cca63c8c7a16278010eceb06d2', '<span style=\"font-family: Arial;\">\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Except where indicated, pricing is per person per round trip flight.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">If you wish to rent a private plane, please contact us!</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Pricing for additional activities like:</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Scuba diving and bone fishing, etc... will be calculated at time of booking. Details, rates and availability are shown in good faith and believed to be reliable, but are not guaranteed. We will confirm rates and availability during the inquiry / booking process.</span></p>\r\n</span>', '2009-01-07 21:22:41', '62', '1');
