INSERT INTO `jos_jf_content` VALUES ('857', '7', '155', 'content', 'title', 'Full Day VIP en Los Roques', '7af7ec49b213af4c100bcada2dbd9bfe', 'Full Day VIP en Los Roques', '2009-01-07 21:29:32', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('858', '7', '155', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Godere di una giornata indimenticabile a belle spiagge di Los Roques, ancora preparato in modo che la vostra unica preoccupazione è quella di rilassarsi e godersi </span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  te. <img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Offriamo la partecipazione dall\'aeroporto in Maiquetía, con biglietto di andata e ritorno a Los Roques, dove si <em><strong> </strong></em>  saranno ricevuti dal nostro equipaggio.  Trasferimento in barca a Craskí la chiave, il pranzo nel ristorante del <em><strong> </strong></em>  Craskí con delizioso pesce fresco, riso, insalate e dessert.  Pranzo box per la spiaggia attrezzata con <em><strong> </strong></em>  ghiaccio, l\'acqua, soda e 2 birre da persona fredda, tende e sedie per la spiaggia, a un\'ora di guida snorkeling nelle acque cristalline della chiave di attenzione personalizzata e professionale bilingue guide durante il viaggio. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>Lunedi a Giovedi Venerdì, Sabato e Domenica</strong> </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>Da Maiquetia National USD.</strong>  <strong>$ 280 USD.</strong>  <strong>$ 295</strong> </span></p>', '0d16661c6770354dd45ce1e186465fbe', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Enjoy of an unforgettable day at the beautiful beaches of Los Roques, prepared yet so that your only preoccupation is to relax and enjoy</span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"> yourself.<img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">We offer attendance from the Airport in Maiquetía, with roundtrip ticket to Los Roques, where you<em><strong> </strong></em>will be received by our Crew. Transfer in boat to the Craskí Key, lunch in the restaurant of<em><strong> </strong></em>Craskí with delicious fresh fish, rice, salad and dessert. Lunch box for the beach equipped with<em><strong> </strong></em>ice, water, soda and 2 cold beers by person, awnings and chairs for beach, one hour of guide snorkeling into the crystalline waters of the key and customized attention of professional bilingual tour guides throughout the trip.</span></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Monday to Thursday Friday, Saturday and Sunday</span></strong></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">From Maiquetia National Airport USD. $280 USD. $295</span></strong></p>', '2009-01-07 21:29:32', '62', '1');
