INSERT INTO `jos_jf_content` VALUES ('928', '7', '154', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">  Volo andata e ritorno: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\"> Caracas </st1:place></st1:city>  / Los Roques </st1:personname><span style=\"mso-spacerun: yes\"></span>  USD $ 255,00 -- <o:p></o:p></span></span></p><ul><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Prezzo per persona. </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  I bambini hanno uno sconto del 30% </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Full Day $ 295.  Con tutto incluso </span></li><li><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">  Privato aereo charter, ti invitiamo a contattarci per un qoute! </span><span style=\"font-size: 10pt;\"></span></span></li></ul><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Parco Nazionale di ingresso non è incluso, si prega di portare valuta locale .... </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  <a href=\"http://www.cacao.travel/en-GB/make-your-reservation-now?task=dobooking&selectedProperty=5\"><strong>Effettuare una prenotazione ora!</strong></a> </span></p>', '75f1ec72d5626eeb475cd5058439a5db', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">Round trip flight: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\">Caracas</st1:place></st1:city> / Los Roques</st1:personname><span style=\"mso-spacerun: yes\">  </span>USD $ 255,00-<o:p></o:p></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Price per person. </span></li>\r\n<li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Children has a discount of 30% </span></li>\r\n<li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Full Day $295. With all included </span></li>\r\n<li><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">Privat air charter, please contact us for a qoute!</span><span style=\"font-size: 10pt;\"> </span></span></li>\r\n</ul>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">National Park entrance fee is not included, please bring local currency....</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\"><strong><a href=\"http://www.cacao.travel/en-GB/make-your-reservation-now?task=dobooking&amp;selectedProperty=5\">Make a reservation right now!</a></strong></span></p>', '2009-01-07 21:29:51', '62', '1');
