INSERT INTO `jos_jf_content` VALUES ('938', '7', '83', 'content', 'introtext', '<span style=\"font-family: Arial;\"><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Tranne dove indicato, i prezzi sono per persona per volo andata e ritorno. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Se si desidera affittare un aereo privato, non esitate a contattarci! </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Le tariffe per altre attività come: </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Immersioni subacquee e di ossa di pesca, ecc ..  sarà calcolato al momento della prenotazione.  Dettagli, prezzi e disponibilità sono mostrati in buona fede e ritenute attendibili, ma non sono garantite.  Si confermano le tariffe e la disponibilità durante l\'inchiesta / processo di prenotazione. </span></p></span>', 'df0347cca63c8c7a16278010eceb06d2', '<span style=\"font-family: Arial;\">\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Except where indicated, pricing is per person per round trip flight.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">If you wish to rent a private plane, please contact us!</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Pricing for additional activities like:</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Scuba diving and bone fishing, etc... will be calculated at time of booking. Details, rates and availability are shown in good faith and believed to be reliable, but are not guaranteed. We will confirm rates and availability during the inquiry / booking process.</span></p>\r\n</span>', '2009-01-07 21:29:53', '62', '1');
