INSERT INTO `jos_jf_content` VALUES ('978', '7', '102', 'content', 'introtext', '<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\">  Se ci deve essere un codice di abbigliamento di impostare lo standard, Los Roques ha alcuni dei migliori climi del mondo e la luce è estate vestiti bene. </span></p><p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\"></span><span style=\"font-size: 9pt; color: #000080;\"><o:p></o:p></span></p>', '51cc8efc6d363c8c6737ab360f205a51', '<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\">If there has to be a dress code you set the standard, Los Roques has some of the best climates in the world and light summer clothes is fine. </span></p>\r\n<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\"></span><span style=\"font-size: 9pt; color: #000080;\"><o:p></o:p></span></p>', '2009-01-07 21:30:02', '62', '1');
