INSERT INTO `jos_jf_content` VALUES ('1178', '9', '154', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">  Ida e volta voo: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\"> Caracas </st1:place></st1:city>  / Los Roques </st1:personname><span style=\"mso-spacerun: yes\"></span>  R $ 255,00 -- <o:p></o:p></span></span></p><ul><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Preço por pessoa. </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  As crianças têm um desconto de 30% </span></li><li><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Dia Inteiro $ 295.  Com tudo incluído </span></li><li><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">  Privado aérea charter, entre em contato conosco para um qoute! </span><span style=\"font-size: 10pt;\"></span></span></li></ul><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Parque Nacional de taxa de matrícula não é incluído, por favor, traga moeda local .... </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  <a href=\"http://www.cacao.travel/en-GB/make-your-reservation-now?task=dobooking&selectedProperty=5\"><strong>Faça uma reserva agora mesmo!</strong></a> </span></p>', '75f1ec72d5626eeb475cd5058439a5db', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: Arial;\">Round trip flight: <st1:personname productid=\"Caracas / Los Roques\" w:st=\"on\"><st1:city w:st=\"on\"><st1:place w:st=\"on\">Caracas</st1:place></st1:city> / Los Roques</st1:personname><span style=\"mso-spacerun: yes\">  </span>USD $ 255,00-<o:p></o:p></span></span></p>\r\n<ul>\r\n<li><span style=\"font-size: 10pt; font-family: arial,helvetica,