INSERT INTO `jos_jf_content` VALUES ('1187', '9', '83', 'content', 'title', 'Quais são seus termos?', 'ecee116f752a9b535a452b1981bc6cd8', 'What are your terms?', '2009-01-07 21:31:25', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('1188', '9', '83', 'content', 'introtext', '<span style=\"font-family: Arial;\"><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Excepto quando indicado em contrário, os preços são por pessoa e por viagem de ida e volta voo. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Se você deseja alugar um avião privado, entre em contato conosco! </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Preços por atividades adicionais como: </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Mergulho e osso pesca, etc ..  será calculado no momento da reserva.  Detalhes, preços e disponibilidade são apresentadas em boa fé e acredita-se ser confiável, mas não são garantidas.  Vamos confirmar tarifas e disponibilidade durante o inquérito / processo de reserva. </span></p></span>', 'df0347cca63c8c7a16278010eceb06d2', '<span style=\"font-family: Arial;\">\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Except where indicated, pricing is per person per round trip flight.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">If you wish to rent a private plane, please contact us!</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Pricing for additional activities like:</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Scuba diving and bone fishing, etc... will be calculated at time of booking. Details, rates and availability are shown in good faith and believed to be reliable, but are not guaranteed. We will confirm rates and availability during the inquiry / booking process.</span></p>\r\n</span>', '2009-01-07 21:31:25', '62', '1');
