INSERT INTO `jos_jf_content` VALUES ('1228', '9', '102', 'content', 'introtext', '<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\">  Se tem de haver uma vestimenta que você definir o padrão, Los Roques tem alguns dos melhores climas do mundo e verão roupas leves é bom. </span></p><p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\"></span><span style=\"font-size: 9pt; color: #000080;\"><o:p></o:p></span></p>', '51cc8efc6d363c8c6737ab360f205a51', '<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\">If there has to be a dress code you set the standard, Los Roques has some of the best climates in the world and light summer clothes is fine. </span></p>\r\n<p><span style=\"font-size: 10pt; color: #000080; font-family: Arial;\"></span><span style=\"font-size: 9pt; color: #000080;\"><o:p></o:p></span></p>', '2009-01-07 21:31:33', '62', '1');
