INSERT INTO `jos_jf_content` VALUES ('2503', '2', '43', 'content', 'introtext', '<span style=\"font-size: 10pt; font-family: Arial;\"><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  <strong>Kære Hacienda Monterosa.</strong> <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  Liz og jeg er så glad for vi gjorde meget muchenjoy denne ferie med dig. <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  Holdningen her er storslået, efter vores første Cuba Libre alle var fine. <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  Fødevaren og Polar øl serveret smagte bedre end andre steder.  Vi både ramte gym, når vi kom hjem, men før, at vi begyndte at spare op til vores næste ferie i Choroni med din (Casa), som base. <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  Den Snorkeling, den udforskning, den mad, dit firma, alt var meget bedre end forventet, du har foretaget vores eventyr sammen sundhedskvalitet (fremragende) Thank you so much. <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">  Oliver og Elizabeth Bonrich. <o:p></o:p></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\"><o:p></o:p></span></p></span>', '36ebff8de278278c88349733de5b7dea', '<span style=\"font-size: 10pt; font-family: Arial;\">\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\"><strong>Dear Hacienda Monterosa.<o:p></o:p></strong></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">Liz and I are so pleased we did very muchenjoy this vacation with you. <o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">The attitude here is magnificent, After our first Cuba Libre all was fine. <o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">The food and the Polar beers served tasted better than anywhere else. We both hit the gym when we got back home, but before that, we started to save for our next vacation in Choroni with your (Casa) as base.<o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">The snorkeling, the exploring, the food, your company, everything was much better than anticipated, you made our adventure together salubrious (superb) Thank you so much.<o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\">Oliver and Elizabeth Bonrich. <o:p></o:p></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt; font-family: Arial;\"><o:p></o:p></span></p>\r\n</span>', '2009-01-14 16:39:21', '62', '1');
