INSERT INTO `jos_jf_content` VALUES ('2573', '2', '83', 'content', 'introtext', '<span style=\"font-family: Arial;\"><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Medmindre andet er fastsat, priser er per person per tur / retur flyvning. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Hvis du ønsker at leje et privat fly, kan du kontakte os! </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Prissætning for yderligere aktiviteter som: </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Dykning og knogler fiskeri osv. ..  vil blive beregnet på tidspunktet for bestilling.  Detaljer, priser og tilgængelighed er vist i god tro og menes at være pålidelige, men er ikke garanteret.  Vi vil bekræfte, priser og tilgængelighed i forbindelse med den undersøgelse / booking processen. </span></p></span>', 'df0347cca63c8c7a16278010eceb06d2', '<span style=\"font-family: Arial;\">\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Except where indicated, pricing is per person per round trip flight.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">If you wish to rent a private plane, please contact us!</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Pricing for additional activities like:</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Scuba diving and bone fishing, etc... will be calculated at time of booking. Details, rates and availability are shown in good faith and believed to be reliable, but are not guaranteed. We will confirm rates and availability during the inquiry / booking process.</span></p>\r\n</span>', '2009-01-14 16:39:41', '62', '1');
