INSERT INTO `jos_jf_content` VALUES ('2593', '2', '99', 'content', 'introtext', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\"><p style=\"margin: 0in 0in 0pt;\" class=\"MsoNormal\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">  De første flyvninger blade på 8.30 om morgenen, og returnerer hverdag på 17,00 timer om eftermiddagen.  Hvis din international flyvning ankommer sent, at dagen kan du overveje at bo på et <a target=\"_blank\" href=\"http://www.caracas.travel\">hotel tæt på lufthavnen i Caracas.</a> </span></p></span>', 'b0016489117be41ab4699cdb334c0333', '<span style=\"font-size: 10pt; color: #003366; font-family: Arial; background-color: #ffffff; mso-fareast-font-family: \'Times New Roman\'; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">\r\n<p style=\"margin: 0in 0in 0pt;\" class=\"MsoNormal\"><span style=\"font-size: 10pt; color: #003366; font-family: Arial;\">The first flights leaves at 8.30 in the morning, and returns everyday at 17.00 hours in the afternoon. If your international flight is arriving late that day you might consider staying in a <a target=\"_blank\" href=\"http://www.caracas.travel\">Hotel close to the airport in Caracas</a>.  </span></p>\r\n</span>', '2009-01-14 16:39:46', '62', '1');
