INSERT INTO `jos_jf_content` VALUES ('2672', '2', '155', 'content', 'title', 'Fuld Day VIP da Los Roques', '7af7ec49b213af4c100bcada2dbd9bfe', 'Full Day VIP en Los Roques', '2009-01-14 16:40:07', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('2673', '2', '155', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Nyd en uforglemmelig dag på det smukke strande i Los Roques, tilberedt endnu, så din eneste bekymring er at slappe af og nyde </span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  dig selv. <img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Vi tilbyder tilstedeværelseslisten fra lufthavnen i Maiquetía med rundtur billet til Los Roques, hvor du <em><strong> </strong></em>  vil blive modtaget af vores besætningsressourcer.  Transfer i båd til Craskí Key, frokost i restauranten i <em><strong> </strong></em>  Craskí med lækker frisk fisk, ris, salat og dessert.  Madpakke til stranden udstyret med <em><strong> </strong></em>  is, vand, sodavand og 2 kolde øl person, markiser og stole til stranden, en times vejledning Snorkeling i krystallinsk farvande af de centrale og tilpassede opmærksomhed professionel tosprogede turistguider hele turen. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>Mandag til torsdag fredag, lørdag og søndag</strong> </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  <strong>Fra Maiquetia National Airport USD.</strong>  <strong>$ 280 USD.</strong>  <strong>$ 295</strong> </span></p>', '0d16661c6770354dd45ce1e186465fbe', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Enjoy of an unforgettable day at the beautiful beaches of Los Roques, prepared yet so that your only preoccupation is to relax and enjoy</span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"> yourself.<img width=\"168\" src=\"images/stories/day_tour.png\" alt=\"day_tour\" height=\"163\" style=\"float: right; margin: 3px;\" /></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">We offer attendance from the Airport in Maiquetía, with roundtrip ticket to Los Roques, where you<em><strong> </strong></em>will be received by our Crew. Transfer in boat to the Craskí Key, lunch in the restaurant of<em><strong> </strong></em>Craskí with delicious fresh fish, rice, salad and dessert. Lunch box for the beach equipped with<em><strong> </strong></em>ice, water, soda and 2 cold beers by person, awnings and chairs for beach, one hour of guide snorkeling into the crystalline waters of the key and customized attention of professional bilingual tour guides throughout the trip.</span></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Monday to Thursday Friday, Saturday and Sunday</span></strong></p>\r\n<p><strong><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">From Maiquetia National Airport USD. $280 USD. $295</span></strong></p>', '2009-01-14 16:40:07', '62', '1');
