INSERT INTO `jos_jf_content` VALUES ('3368', '6', '153', 'content', 'title', 'Comment se rendre à l\'aéroport de Caracas', 'c091b3a54858a873590f712904238ee4', 'How to get to Caracas Airport', '2009-02-10 02:47:53', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('3369', '6', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Comment se rendre à l\'Aéroport National de Caracas (Maiquetia). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 heure de Caracas, en utilisant la route nationale, s\'il vous plaît dites au chauffeur de taxi pour aller à l\'aéroport national. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  S\'il vous plaît être ici 1 heure avant votre départ, s\'il vous plaît faire votre réservation de vol aujourd\'hui, là où jusqu\'à 2 semaines des listes d\'attente pour les vols au Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional. </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela... </span></p>', '2009-02-10 02:47:53', '62', '1');
