INSERT INTO `jos_jf_content` VALUES ('3418', '2', '151', 'content', 'title', 'Private fly', '0bad2fd6877462be47ca1af3bbf0b7e5', 'Private Planes', '2009-02-10 02:48:23', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('3419', '2', '151', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  For vores VIP-passagerer, vi tilbyder vores nye private luftfartsaftaler, og en ny Corporative tjeneste Jet charter og <img width=\"224\" src=\"images/stories/jet_charter.png\" alt=\"jet_charter\" height=\"146\" style=\"float: right; margin: 3px;\" />  udøvende Bimotors for nationale og internationale ind og ud af Venezuela.  Førende turisttjenester i Venezuela, med mere end 15 år på markedet, er vi udvide vores flåde af private fly til at opfylde de nye krav af udøvende rejse markedet i dag, tilbyder højeste service 7 dage i ugen, 24 timer om dagen . </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Bare lad os vide, hvor du vil hen, og hvordan du ønsker at flyve, og vi vil være ansvarlige for alle dine krav. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Lad os hjælpe dig ud, fordi vores Service VIP, vil give dig hvad du søger. </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><img width=\"224\" src=\"images/stories/air_charter.png\" alt=\"air_charter\" height=\"147\" style=\"float: left; margin: 3px;\" /></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Vi tilbyder små fly og kontaktpersoner jetfly, for udgange fra alle lufthavne med ekstra tjenester inde i fly (catering), altid med henblik på at tilfredsstille vores kunder, og hans mere intime nydelser. </span></p><p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\">  <strong>Air-fly til privat chartere:</strong> King, Cessna 406, Bi motor.Citation Jet Cj1, år 2007 </span></span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  <strong>En times flyvning til enhver national destination om bord på et Private Leer konge med kapacitet til 7 personas vil koste dig $ 875, --</strong> </span></p>', 'c5692802d80fcec172822136965cf208', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">For our VIP passengers we offer our new private air service, and a new Corporative service of Jet charter and<img width=\"224\" src=\"images/stories/jet_charter.png\" alt=\"jet_charter\" height=\"146\" style=\"float: right; margin: 3px;\" /> executive Bimotors for national and international in and out of Venezuela. Leading tourist services in Venezuela, with more than 15 years in the market, we are extending our fleet of private planes to fulfill the new exigencies of executive travel market of today, offering a highest service 7 days of the week, 24 hours of the day.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Just let us know where you want to go and how you wish to fly and we will be in charge of all your demands.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Let us help you out, because our Service VIP, will give you what you are looking for.</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><img width=\"224\" src=\"images/stories/air_charter.png\" alt=\"air_charter\" height=\"147\" style=\"float: left; margin: 3px;\" /></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">We offer small planes and executives jets, for exits from any airport with extra services inside the plane (Catering), always in order to please our clients and his more intimate pleasures.</span></p>\r\n<p><span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt;\"><strong>Air planes for private charters:</strong> King, Cessna 406, Bi motor.Citation Jet Cj1, year 2007</span></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\"><strong>One hour flight to any national destination on board a Private Leer king with capacity for 7 personas will cost you $875,-</strong></span></p>', '2009-02-10 02:48:23', '62', '1');
