INSERT INTO `jos_jf_content` VALUES ('3429', '2', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Hvordan du kommer til Caracas National Airport (Maiquetia). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 time fra Caracas bruger National Highway, bedes du fortælle taxachauffør for at gå til Aeropuerto nacional. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  Vær her 1 time før din afrejse, kan du gøre din flyreservation, nu, dér, hvor op til 2 uger ventelister for nationale flyvninger i Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional. </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela... </span></p>', '2009-02-10 02:48:25', '62', '1');
