INSERT INTO `jos_jf_content` VALUES ('3638', '9', '153', 'content', 'title', 'Como chegar a Caracas Aeroporto', 'c091b3a54858a873590f712904238ee4', 'How to get to Caracas Airport', '2009-02-10 02:51:35', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('3639', '9', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Como chegar ao aeroporto de Caracas Nacional (Maiquetia). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 hora de Caracas utilizando a rodovia nacional, por favor informe o taxista para ir para o Aeroporto Nacional. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  Pode estar aqui 1 horas antes da sua partida, por favor, faça o seu voo reserva agora, lá onde até 2 semanas listas de espera para vôos nacionais na Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional. </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela... </span></p>', '2009-02-10 02:51:35', '62', '1');
