INSERT INTO `jos_jf_content` VALUES ('3779', '2', '110', 'content', 'introtext', '<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"><p> <strong><img width=\"300\" src=\"images/stories/maiquetia_airport_venezuela.jpg\" alt=\"maiquetia_airport_venezuela\" height=\"196\" style=\"float: right; margin: 3px; border: #bbbbbb 2px solid;\" /></strong> </p><p><span><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  De bedste tilbud fra Venezuela Flyvninger, nu kan du endelig gøre og online-reservation, til næsten enhver destination i Venezuela .... <br />  Flyvninger i Venezuela.  Find og sammenligne tilbud på billige flyrejser af lave omkostninger og nationale luftfartsselskaber.  Vælg den der flyver ud og ankommer lufthavn og kun et par trin får du et stort udvalg af de bedste flyvning billetpriser anvendes af lave omkostninger og nationale luftfartsselskaber.  Du kan bestille din flyrejse online i fuld sikkerhed og købe flybilletter på kun et par minutter. </span></span></p><p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p><p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p><p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\">  <a target=\"_blank\" href=\"en/book-your-flight-now\" title=\"Book now\">Foretage en reservation!</a> <a href=\"en/how-to-get-to-los-roques/sitemap\">Sitemap</a> </span></span></span></span></span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></p></span></span>', 'e2663784a49438a76ac452f1930f45fe', '<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">\r\n<p><strong><img width=\"300\" src=\"images/stories/maiquetia_airport_venezuela.jpg\" al