INSERT INTO `jos_jf_content` VALUES ('3929', '2', '70', 'content', 'introtext', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  Imens <st1:personname productid=\"a yacht charter\" w:st=\"on\">  et stort eventyr ferie </st1:personname>  erfaring ikke er til hverdag person ultimative luksus kommer med en pris.  Erkendtlighed har altid været et ømt emne, når det kommer til reelle rejse industrien og i de fleste VIP rejseerfaringer. <o:p></o:p></span></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  For særlige <st1:personname productid=\"Charter vacations\" w:st=\"on\">  ferier </st1:personname>  , Udgifter er sjældent &quot;all inclusive&quot;, så du bedes venligst forlade noget til vicevært, kok eller en hjælpende hånd, når du forlader. </span></span></p>', '38baea938d1777300b362f1990c41139', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">While <st1:personname productid=\"a yacht charter\" w:st=\"on\">a great adventure vacation </st1:personname>experience is not for the everyday person, ultimate luxury does come with a price. Gratuity has always been a sore subject when it comes to the real travel industry and in most VIP travel experiences. <o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">For special <st1:personname productid=\"Charter vacations\" w:st=\"on\">vacations</st1:personname>, expenses are rarely all inclusive, so please leave something to the caretaker, cook or any helping hand when you leave. </span></span></p>', '2009-02-10 10:58:14', '62', '1');
