INSERT INTO `jos_jf_content` VALUES ('3958', '2', '106', 'content', 'title', 'Hvilken slags aktiviteter er der for at gøre i Caracas?', 'e029e8844141d9709370ce57c2a48c34', 'What kind of activities is there to do in Caracas?', '2009-02-10 10:58:22', '62', '1');
INSERT INTO `jos_jf_content` VALUES ('3959', '2', '106', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Aktiviteter i og omkring Caracas og generelt i Venezuela er mange og varierede.  For eksempel stor vandsport er muligt: kitesurfing, Snorkeling, dykning, Bone fiskeri og windsurfing.  Vi vil også forsøge at tage højde for alle andre aktiviteter, kan du anmode om på tidspunktet for bestilling. </span></p>', '48a67dcf9e1c5491eaab2c0a1bca2976', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">Activities in and around Caracas and generally in Venezuela are many and varied. For example great water sports are possible: Kitesurfing, Snorkeling, Scuba Diving, Bone fishing, and Windsurfing. We will also try to accommodate any other activities you may request at time of booking.</span></p>', '2009-02-10 10:58:22', '62', '1');
