INSERT INTO `jos_jf_content` VALUES ('3969', '5', '70', 'content', 'introtext', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  Mientras <st1:personname productid=\"a yacht charter\" w:st=\"on\">  una gran aventura de vacaciones </st1:personname>  experiencia no es para la persona todos los días, en última instancia de lujo no vienen con un precio.  La gratuidad ha sido siempre un dolor de tema cuando se trata de la verdadera industria de viajes y en la mayoría de experiencias de viaje VIP. <o:p></o:p></span></span></p><p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">  Especiales <st1:personname productid=\"Charter vacations\" w:st=\"on\">  vacaciones </st1:personname>  , Rara vez son los gastos de todo incluido, así que por favor dejar algo al conserje, cocinar o ayudar a cualquier parte cuando salga. </span></span></p>', '38baea938d1777300b362f1990c41139', '<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">While <st1:personname productid=\"a yacht charter\" w:st=\"on\">a great adventure vacation </st1:personname>experience is not for the everyday person, ultimate luxury does come with a price. Gratuity has always been a sore subject when it comes to the real travel industry and in most VIP travel experiences. <o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\"><span style=\"font-size: 10pt;\"><span style=\"font-family: arial,helvetica,sans-serif;\">For special <st1:personname productid=\"Charter vacations\" w:st=\"on\">vacations</st1:personname>, expenses are rarely all inclusive, so please leave something to the caretaker, cook or any helping hand when you leave. </span></span></p>', '2009-02-10 11:00:11', '62', '1');
