INSERT INTO `jos_yos_elements_properties` VALUES ('1', 'title', 'String', '0', '', 'Movie title. Title can be displayed in a roll-over balloon.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('2', 'description', 'String', '1', '', 'Include description of object', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('3', 'alpha', 'Number', '0', '100', 'Opacity of a movie. 0 for invisible (but still clickable).', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('4', 'color', 'Hex color code', '0', '', 'If you do not set color, the movie will keep its original color.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('5', 'remain', 'true / false', '0', 'true', '\"true\" means that this movie will remain visible when user clicks on it or on other objects in the same level. If your movie clip has child objects, you might want to make it invisible - set this property to \"false\" then.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('6', 'center', 'true / false', '0', 'false', '\"true\" means that the movie center will be in specified longitude & latitude (or x & y). Otherwise movie\'s top left corner will be in this position. It is recommended to set \"true\" for photos, images and custom swf movies. However default amMap icons are made so that the icon\'s center is in 0,0 position, that\'s why you shouldn\'t set this setting to \"true\" when you use these icons.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('7', 'x', 'Number, Number% or !Number', '0', '', 'If you want your movie to be bounded to the stage, set x and y properties. This means that this movie will not move together with the map. To find desired x and y, use developer mode.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('8', 'color_hover', 'Hex color code', '0', '', 'Movie color when user roll-overs it. Movie will not change color if this property is not set.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('9', 'oid', 'String', '0', '', 'Unique object id. You only need it if you want to make \"link\" from one object to another.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('10', 'file', 'String', '0', '', 'File name of a movie. This file should be placed in \"path\" folder (defined in map html file). If you want to use embedded movie, set movie name here: file=\"rectangle\" (circle, border, target, home)', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('11', 'y', 'Number, Number% or !Number', '0', '', 'If you want your movie to be bounded to the stage, set x and y properties. This means that this movie will not move together with the map. To find desired x and y, use developer mode.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('12', 'lat', 'Number', '0', '', 'If you want your movie to be bounded to the map, set lat and long (latitude and longitude) properties. This means that this movie will move together with the map. To find desired lat and long, use developer mode.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('13', 'long', 'Number', '0', '', 'If you want your movie to be bounded to the map, set lat and long (latitude and longitude) properties. This means that this movie will move together with the map. To find desired lat and long, use developer mode.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('14', 'rotation', 'Number', '0', '', 'Movie clip rotation in degrees.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('15', 'height', 'Number or Number%', '0', '', 'Width and height of your movie. If you want to keep original size, do not set these properties.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('16', 'width', 'Number or Number%', '0', '', 'Width and height of your movie. If you want to keep original size, do not set these properties.', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('17', 'fixed_size', 'true / false', '0', 'false', '\"false\" means that the movie will be resized together with the map (when zooming). If you want your movie to remain the same size, set this property to \"true\".', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('18', 'url', 'String', '0', '', 'There are several options: 1) web site address: http://www.ammap.com 2) JavaScript function: javascript:alert(\'movie clicked\'); 3) #top - will go to top level of your map 4) #parent - will go one level up 5) #object_id - will act as if object with oid=\"object_id\" was clicked', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('19', 'flash_vars', 'String', '0', '', 'If you use amMap on a web server, you can pass variables to your movies by adding them to your file name, for example: file=\"movie.swf?id=50&color=#CC0000\". However if you open ammap as a stand alone flash project, this method will not work. The parameter \"flash_vars\" will help you to solve this problem. You can pass any number of variables to your movie: flash_vars=\"id=10&color=#CC0000\".', 'movies', '0', '0000-00-00 00:00:00');
INSERT INTO `jos_yos_elements_properties` VALUES ('20', 'target', 'String', '0', '', 'Target of url. Will work only if url is web site address. Do not set any target if you want this page to be opened in the same window. For a new window use \"_blank\".', 'movies', '0', '0000-00-00 00:00:00');
