INSERT INTO `jos_content` VALUES ('110', 'Venezuela Flights and Airline companies 2010', 'venezuela-flights-and-airlines-2009', '', '<span style=\"font-family: arial,helvetica,sans-serif;\"><span style=\"font-family: arial,helvetica,sans-serif; font-size: 10pt;\">\r\n<p><strong><img height=\"196\" width=\"300\" src=\"images/stories/maiquetia_airport_venezuela.jpg\" alt=\"maiquetia_airport_venezuela\" style=\"float: right; margin: 3px; border: #bbbbbb 2px solid;\" /></strong></p>\r\n<p><span><span style=\"font-family: arial,helvetica,sans-serif; font-size: 10pt;\">The best offers from Venezuela Flights, now you can finally make and online reservation to almost any destination in Venezuela....<br />Flights in Venezuela. Find and compare the offers for cheap flights of low cost and national airlines. Select the flying out and arriving airport and in only a few steps you get a large selection of the best flight fares applied by low cost and national airlines. You can book your flight online in full security and purchase the flight tickets in only a few minutes.</span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></span></p>\r\n<p><span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"><a target=\"_blank\" href=\"en/book-your-flight-now\" title=\"Book now\">Make a Reservation!</a>Â  <a href=\"en/how-to-get-to-los-roques/sitemap\">Sitemap</a></span></span></span></span></span><span><span><span><span style=\"font-family: arial,helvetica,sans-serif;\"></span></span></span></span></p>\r\n</span></span>', '', '1', '4', '0', '25', '2009-02-09 00:00:00', '62', 'Venezuela National Flights', '2010-03-01 17:11:47', '62', '0', '0000-00-00 00:00:00', '2008-09-22 23:50:48', '0000-00-00 00:00:00', '', '', 'show_title=1\nlink_titles=1\nshow_intro=0\nshow_se