INSERT INTO `jos_jf_content` VALUES ('3299', '5', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  CÃ³mo llegar al aeropuerto de Caracas (MaiquetÃ­a). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 hora de Caracas utilizando la carretera nacional, por favor, decirle al conductor de taxi para ir al Aeropuerto nacional. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  Por favor, aquÃ­ 1 hora antes de su partida, por favor, haga su reserva de vuelo ahora, allÃ­ donde hasta 2 semanas de las listas de espera para vuelos nacionales en Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional.Â </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela...Â </span></p>', '2009-02-10 02:47:17', '62', '1');
