INSERT INTO `jos_jf_content` VALUES ('3499', '3', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Wie kommt man zum Flughafen Caracas (Maiquetia). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 Stunde von Caracas mit der nationalen Autobahn, sagen Sie dem Taxifahrer, um zum Aeropuerto Nacional. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  Bitte hier 1 Stunde vor Ihrer Abreise, Ã¼berprÃ¼fen Sie bitte, buchen Sie Ihren Flug jetzt, dort, wo bis zu 2 Wochen Wartelisten fÃ¼r nationale FlÃ¼ge in Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional.Â </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela...Â </span></p>', '2009-02-10 02:50:02', '62', '1');
