INSERT INTO `jos_jf_content` VALUES ('3569', '7', '153', 'content', 'introtext', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  Come raggiungere l\'aeroporto di Caracas Nazionale (MaiquetÃ­a). </span></p><p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">  1 / 2 ora da Caracas utilizzando la strada statale, informi il Taxi driver per andare al Aeropuerto nazionale. </span></p><p><span style=\"font-size: 10pt; font-family: Arial;\">  Si prega di essere qui 1 ora prima della partenza, si prega di effettuare la tua prenotazione di volo adesso, lÃ  dove fino a 2 settimane liste di attesa per i voli nazionali in Venezuela ... </span></p>', '4b18977f6bfe878d33fc83559c923151', '<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\"></span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">How to get to the Caracas National Airport (Maiquetia).</span></p>\r\n<p><span style=\"font-size: 10pt; font-family: arial,helvetica,sans-serif;\">1/2 hour from Caracas using the national highway, please tell the Taxi driver to go to the Aeropuerto nacional.Â </span></p>\r\n<p><span style=\"font-size: 10pt; font-family: Arial;\">Please be here 1 hour before your departure, please make your flight reservation now, there where up to 2 weeks waiting lists for national flights in Venezuela...Â </span></p>', '2009-02-10 02:50:49', '62', '1');
